import React from 'react';
import Train from '../components/Train.js';
import arrivals from '../server/trainData.js';

export default function TrainList(props) {
  const color = props.color;
  const filter = props.filter;

  return (
    <div>
      {arrivals['RailArrivals'].map((arrival) => {
        // Add logic to filter based on the selected line and filter
        if (
          (arrival.LINE === color) &&
          ((filter === 'Arriving' && arrival.STATUS === 'ARRIVING') ||
            (filter === 'Scheduled' && arrival.STATUS === 'SCHEDULED') ||
            (filter === 'Northbound' && arrival.DIRECTION === 'NORTHBOUND') ||
            (filter === 'Southbound' && arrival.DIRECTION === 'SOUTHBOUND') ||
            (filter === 'Eastbound' && arrival.DIRECTION === 'EASTBOUND') ||
            (filter === 'Westbound' && arrival.DIRECTION === 'WESTBOUND'))
        ) {
          return <Train {...arrival} key={arrival.TRAIN_ID} />;
        }
        return null; // Return null for trains that don't match the condition
      })}
    </div>
  );
}
